// });
var AWS = require('aws-sdk');
var s3 = new AWS.S3({accessKeyId:'AKIAXKCRRSD3GJHJE65C', secretAccessKey:'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', region:'us-west-2'});
var urlparams = {Bucket: 'max-client-logos', Key: allKeys[0]};
s3.getSignedUrl('getObject', urlparams, function (err, url) {
  console.log('Your generated pre-signed URL is', url);
});

class Logo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      choice:"", //Need to somehow set up the initial state so that the user can see the currewnt stage of the logo
    }
  };

  handleChange = (option) => {
    const value = option.target.value;
    this.setState({choice:value});
  }

  render(){
    const allURL = downloadS3(this.props.prefix,this.props.bucketName);
    console.log(allURL);
    const logoList = allURL.map((url) =>
    <label className="radio-inline">
      <input key={uniqueId()} type="radio" name="inlineRadioOptions" value={url} onChange={this.handleChnage}>
        <img src={url} alt="" />
      </input>
    </label>
  );
    return(
      <div id="logo" className="logo">
          <h3>Logo</h3>
          <span className="help-block" id="helpBlock">Choose page logo</span>
          <div className="logo-options">
            {logoList}
          </div>
          <br/>
      </div>
    );
  }
}

export default Logo;
