import React from "react";
import uniqueId from "lodash/uniqueId";
import { ChromePicker } from "react-color";

// function generator() {
//     document.getElementById("cp1").colorpicker();
// }
//
// generator();

class ColorpickerComponent extends React.Component {
    render() {
        const {
            value,
            className,
            title,
            handleClick,
            handleChange,
            handleClose,
            displayColorPicker
        } = this.props;
        return (
            <div className={className}>
                <h5>{title}</h5>
                <div className="input-group colorpicker-container">
                    <input
                        type="text"
                        className="form-control colorpicker-text"
                        defaultValue={value}
                        name={title}
                        onClick={handleClick}
                        onChange={handleChange}
                    />
                    <span className="input-group-addon">
                        <i>
                            {displayColorPicker ? (
                                <div className="colorpicker-palette">
                                    <div
                                        className="colorpicker-cover"
                                        onClick={handleClose}
                                    />
                                    <ChromePicker
                                        onChangeComplete={handleChange}
                                    />
                                </div>
                            ) : null}
                        </i>
                    </span>
                </div>
            </div>
        );
    }
}

class Colors extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            displayColorPicker: false,
            Background: "#fff",
            Header: "#fff",
            SelectionButton: "#fff"
        };
    }

    handleClick = () => {
        this.setState({ displayColorPicker: !this.state.displayColorPicker });
    };

    handleClose = () => {
        this.setState({ displayColorPicker: false });
    };
    //Can also use handleChange but that will change the state everytime we call on drag events
    handleChangeComplete = (color, event) => {
        if (event.target.name === "Background") {
            this.setState({ Background: color.hex });
        } else if (event.target.name === "Header") {
            this.setState({ Header: color.hex });
        } else if (event.target.name === "Selection Button") {
            this.setState({ SelectionButton: color.hex });
        } else {
            //do nothing
        }
    };

    render() {
        return (
            <div id="colors" className="colors">
                <h4>Colors</h4>
                <span className="help-block" id="helpBlock">
                    Change the color of the page
                </span>
                <ColorpickerComponent
                    className="background-color"
                    value={this.state.Background}
                    title="Background"
                    onChange={this.handleChangeComplete}
                    onClick={this.handleClick}
                    displayColorPicker={this.state.displayColorPicker}
                    handleClose={this.handleClose}
                />
            </div>
        );
    }
}

export default Colors;
