import React from "react";
// import Ordering from "./Ordering";
import Sortable from 'react-sortablejs';
// import Sortable from 'sortablejs';

class FundSelection extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      optionList: [{ name: "Option 1", isChecked: false }, { name: "Option 2", isChecked: false }, { name: "Option 3", isChecked: false }],
      optionsChecked: []
    }
  }

  onAddingOption = (option) => {
    const isChecked = option.target.checked;
    const value = option.target.value;

    this.setState(prevState => ({ optionList: prevState.optionList.map(option => option.name === value ? { ...option, isChecked: isChecked } : option) }));

    if (isChecked)
      this.setState(prevState => ({optionsChecked: [...prevState.optionsChecked, value] }));
    else {
      const newoptionsChecked = this.state.optionsChecked.filter(option => option !== value)
      this.setState({ optionsChecked: newoptionsChecked });
    }
  }



  render() {
    const option_list = this.state.optionList.map((option, i) =>
      <label key={i+1} data-id={i+1} className="checkbox-inline">
        <input type="checkbox" value={option.name} checked={option.isChecked} onChange={this.onAddingOption} />Option {i+1}
      </label>
    );
    const order_list = this.state.optionsChecked.map((option, i) =>
    <li key={i+1} data-id={i+1} className="list-group-item">
      {this.state.optionsChecked[i]}
    </li>
      );

    return (
      <div>
        <div className="apperance">
          <h4>Apperance</h4>
          <span className="help-block" id="helpBlock">Choose which funds are visible on the page</span>
          <div className="multi-selection-fund">
            {option_list}
          </div>
          <div>
            <h4>Ordering</h4>
            <span class="help-block" id="helpBlock">Choose which order the funds are displayed on the page</span>
            <div id="simpleList" className="list-group">
              <Sortable
                options={{animation:150}}
                className="block-list"
                tag="ul"
                ref={(c) => {
                  if (c) {
                    this.order_list = c.sortable;
                  }
                }}
                onChange={(options => {
                  this.setState({optionsChecked:options});
                })}
              >
                <ul>
                  {order_list}
                </ul>
              </Sortable>
          </div>
          </div>
        </div>
      </div>
    )
  }
}


export default FundSelection;
