/* eslint-disable no-unused-expressions */
import React from "react";
import uniqueId from 'lodash/uniqueId';



class Frequency extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      frequencyList : [{name:"One-time", isChecked: false}, {name:"Recurring",isChecked:false}],
      frequencyChecked : [],
      frequencyOptions : [{name:"Weekly", isChecked: false}, {name:"Monthly",isChecked:false}, {name:"Twice Montly", isChecked: false}, {name:"Every Other Week",isChecked:false}],
      frequencyOptionsChecked : [],
      totalFrequencyOptionsChecked : 0,
      errorMessage : ""
    }
  }

    addFrequency = (option) => {
      const isChecked = option.target.checked;
      const value = option.target.value;

      this.setState(prevState => ({ frequencyList: prevState.frequencyList.map(option => option.name === value ? { ...option, isChecked: isChecked } : option) }));

      if (isChecked){
          this.setState(prevState => ({frequencyChecked: [...prevState.frequencyChecked, value] }));
          // if(value === "Recurring"){
          //   renderOptions();
          // }
      }
      else {
          const newfrequencyChecked = this.state.optionsChecked.filter(option => option !== value);
          this.setState({ frequencyChecked: newfrequencyChecked });
      }
    }

    renderOptions = () => {
      const frequency_options_list = this.state.frequencyOptions.map((frequency_option) =>
        <label className="checkbox-inline" htmlFor="fund-selection">
          <input type="checkbox" checked={frequency_option.isChecked} value={frequency_option.name} onChange={this.addFrequencyOptions} />{frequency_option.name}
        </label>
      );

      return (
        <div className="frequency-option">
          <h4>Frequency Options</h4>
          <div className="multi-selection-frequency-option">
            {frequency_options_list}
          </div>
        </div>
      )
  }

    addFrequencyOptions = (frequency_option) => {
      let {totalFrequencyOptionsChecked} = this.state;
      const isChecked = frequency_option.target.checked;
      const minCount = 1;
      const value = frequency_option.target.value;

      this.setState(prevState => ({ frequencyOptions: prevState.frequencyOptions.map(option => option.name === value ? { ...option, isChecked: isChecked } : option) }));

      if(isChecked){
        this.setState(prevState => ({frequencyOptionsChecked: [...prevState.frequencyOptionsChecked, value] }));
        totalFrequencyOptionsChecked += 1;
      }
      else {
        const newfrequencyOptionsChecked = this.state.frequencyOptionsChecked.filter(option => option !== value)
        this.setState({ frequencyOptionsChecked: newfrequencyOptionsChecked });
        totalFrequencyOptionsChecked -= 1;
      }

      //Error in selectoin count
      if (totalFrequencyOptionsChecked < minCount){
        // Is below min
        this.setState({
          errorMessage: `Must have at least ${minCount} checkboxe selected.`,
          totalFrequencyOptionsChecked,
        });
      }
      else {
      // Valid
      this.setState({
        errorMessage: '',
        totalFrequencyOptionsChecked:totalFrequencyOptionsChecked,
      });
    }
      return(
        <section>
          <p>{this.state.errorMessage}</p>
        </section>
      )
  }

    render(){



      const frequency_list = this.state.frequencyList.map((frequency) => {
        if (frequency.name === "Recurring") {
          <label key={uniqueId()} className="checkbox-inline" htmlFor="fund-selection">
            <input type="checkbox" value={frequency.name} checked={frequency.checked} onClick={this.renderOptions} onChange={this.addFrequency} />{frequency.name}
          </label>
        }
        else{
          <label key={uniqueId()} className="checkbox-inline" htmlFor="fund-selection">
            <input type="checkbox" value={frequency.name} checked={frequency.checked} onChange={this.addFrequency} />{frequency.name}
          </label>
        }
      });

      return (
        <div className="frequency">
          <h4>Frequency</h4>
          <span class="help-block" id="helpBlock">Choose which frequency option are available to the user</span>
          <div class="multi-selection-frequency">
            {frequency_list}
          </div>
        </div>
      )
    }
}

export default Frequency;
