import React from "react";
import uniqueId from 'lodash/uniqueId';
import { SketchPicker } from 'react-color';

class ColorpickerComponent extends React.Component {
  colorpicker = () => {
    document.getElementById(props.id).colorpicker();
  }

  render() {
    const {value, className, title, handleChange} = this.props;
    return(
      <div className={className}>
        <h5>{title}</h5>
        <div className="input-group colorpicker-component">
          <input id="cp1" type="text" className="form-control" defaultValue={value} name={title} onChange={handleChange}/>
          <span className="input-group-addon"><i></i></span>
        </div>
      </div>
    )
  }
}

