var AWS = require("aws-sdk");
const s3 = new AWS.S3();
s3.config.update({
    accessKeyId: "AKIAXKCRRSD3GJHJE65C",
    secretAccessKey: "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",
    region: "us-west-2"
});

function listAllKeys(params, outPutArray) {
    s3.listObjectsV2(params, function(err, data) {
        if (err) {
            console.log(err, err.stack);
        } else {
            var contents = data.Contents;
            contents.forEach(function(content) {
                outPutArray.push(content.Key);
                console.log("1", outPutArray);
            });

            if (data.IsTruncated) {
                params.ContinuationToken = data.NextContinuationToken;
                console.log("There are more keys coming up");
                listAllKeys(params, outPutArray);
            }
        }
    });
    console.log("2", outPutArray);
}

