import React, { useState, useEffect } from "react";

class OrderingList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      optionList:[{name:"Option 1", isChecked:false},{name:"Option 2", isChecked:false},{name:"Option 3", isChecked:false}, {name:"Option 4", isChecked: true}],
      optionChecked:[],
    }
  }

  AddingOption = (option) => {
    const isAdded = option.target.checked;
    const value = option.target.value;

    this.setState(prevState => ({optionList: prevState.optionList.map(option => option.name === value ? {...option, isChecked: isAdded} : option)}));

    if (isAdded)
    {
      this.setState(prevState => ({itemChecked: [...prevState.optionChecked, value]}));
    }
    else {
      const newAddedOptions = this.state.optionChecked.filter(option => option !== value);
      this.setState({optionChecked: newAddedOptions});
    }
  }

    render(){
      const {optionList} = this.state;
      const option_list = optionList.map((index,i) =>
        <label className="checkbox-inline">
          <input type="checkbox" id="inlineCheckbox" checked={this.state.optionList[i].isChecked}  value={index.name} onChange={this.AddingOption} />Option {i+1}
        </label>
      );
      return(
        <div className="multi-selection-fund">
          {option_list}
          <div>
            Added Options: {this.state.optionChecked.join(', ')}
          </div>
        </div>
      )
    }
}

export default OrderingList;
