import React from "react";
import uniqueId from 'lodash/uniqueId';
import hidediv from './hidediv.js';

class Checkbox extends React.Component {
  render() {
    const { isChecked, onCheckboxChange, name, value } = this.props;

    return (
      <label key={uniqueId()} className="checkbox-inline" htmlFor="frequency-selection">
        <input
          value={value}
          checked={isChecked}
          onChange={onCheckboxChange}
          onBlur={onCheckboxChange}
          type='checkbox'
        />{name}
      </label>
    );
  }
}

class Frequency_options extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      frequencyOptions : [{name:"Weekly", isChecked: false}, {name:"Monthly",isChecked:false}, {name:"Twice Montly", isChecked: false}, {name:"Every Other Week",isChecked:false}],
      frequencyOptionsChecked : [],
      totalFrequencyOptionsChecked : 0,
      errorMessage : `Must have at least ${props.minCount} checkboxe selected.`
    }
    this.addFrequencyOptions = this.addFrequencyOptions.bind(this);
  }

  addFrequencyOptions(event) {
    const {minCount} = this.props;
    let {totalFrequencyOptionsChecked} = this.state;
    const isChecked = event.target.checked;
    const value = event.target.value;

    if (event.type === 'change') {
      if (event.currentTarget.checked) {
        totalFrequencyOptionsChecked += 1;
      } else {
        totalFrequencyOptionsChecked  -= 1;
      }
    }

    this.setState(prevState => ({ frequencyOptions: prevState.frequencyOptions.map(option => option.name === value ? { ...option, isChecked: isChecked } : option) }));

    if(isChecked){
      this.setState(prevState => ({frequencyOptionsChecked: [...prevState.frequencyOptionsChecked, value] }));
    }
    else {
      const newfrequencyOptionsChecked = this.state.frequencyOptionsChecked.filter(option => option !== value)
      this.setState({ frequencyOptionsChecked: newfrequencyOptionsChecked });
    }

    //Error in selectoin count
    if (totalFrequencyOptionsChecked < minCount){
      // Is below min
      this.setState({
        errorMessage: `Must have at least ${minCount} checkboxe selected.`,
        totalFrequencyOptionsChecked:totalFrequencyOptionsChecked
      });
    }
    else {
    // Valid
      this.setState({
        errorMessage: "",
        totalFrequencyOptionsChecked:totalFrequencyOptionsChecked
      });
    }
  }
  render() {
    const { errorMessage } = this.state;
    // let errorMessage = this.state.errorMessage !== "" ? <p>{errorMessage}</p> : null;

    // if(!this.props.isRecurringChecked){
    //   return null;
    // }

    return (
      <div className="frequency-option">
        <h4>Frequency Options</h4>
        <div className="multi-selection-frequency-option">
          <section>
            <p>{errorMessage}</p>
            {this.state.frequencyOptions.map((option) => (
              <Checkbox
                key={uniqueId()}
                value={option.name}
                isChecked={option.isChecked}
                onCheckboxChange={this.addFrequencyOptions}
                name={option.name}
              />
            ))}
          </section>
        </div>
      </div>
    )
  }
}

