import React from "react";
import uniqueId from "lodash/uniqueId";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

let base64 = require("base-64");
let url = "https://test-api.mobileaxept.com/micro_service/page-properties";
let username = "spencer.grimes@mobileaxept.com";
let password = "Temp654321";

let headers = {
    Authorization: "Basic " + base64.encode(username + ":" + password),
    "Content-Type": "application/json"
};

const propertyArray = ["suppressRecurring", "onlyRecurring"];
const length = propertyArray.length;
// const propertyLibrary = {
//     suppressRecurring: "One-time",
//     onlyRecurring: "Recurring"
// };

class Checkbox extends React.Component {
    render() {
        const { isChecked, onCheckboxChange, name, value } = this.props;

        return (
            <label
                key={uniqueId()}
                className="checkbox-inline"
                htmlFor="frequency-selection"
            >
                <input
                    value={value}
                    checked={isChecked}
                    onChange={onCheckboxChange}
                    onBlur={onCheckboxChange}
                    type="checkbox"
                />
                {name}
            </label>
        );
    }
}

class FrequencyOptions extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            frequencyOptions: [
                { name: "Weekly", isChecked: false },
                { name: "Every Other Week", isChecked: false },
                { name: "Twice Montly", isChecked: false },
                { name: "Monthly", isChecked: false }
            ],
            frequencyOptionsChecked: [],
            totalFrequencyOptionsChecked: 0,
            errorMessage: `Must have at least ${props.minCount} checkbox selected.`
        };
        this.addFrequencyOptions = this.addFrequencyOptions.bind(this);
    }

    componentDidMount() {
        const client_code = this.props.clientCode;
        const pageId = this.props.pageId;
        const field = "frequencyChoices";
        let newUrl =
            url +
            "?client_code=" +
            client_code +
            "&page_id=" +
            pageId +
            "&field=" +
            field;
        fetch(newUrl, {
            method: "GET",
            headers: headers,
            mode: "cors"
        })
            .then(response => {
                if (response.ok) {
                    return response.json();
                } else {
                    throw new Error("Something went wrong ...");
                }
            })
            .then(data => {
                console.log(data);
            });
    }

    addFrequencyOptions(event) {
        const { minCount } = this.props;
        let { totalFrequencyOptionsChecked } = this.state;
        const isChecked = event.target.checked;
        const value = event.target.value;

        if (event.type === "change") {
            if (event.currentTarget.checked) {
                totalFrequencyOptionsChecked += 1;
                this.setState({
                    totalFrequencyOptionsChecked: totalFrequencyOptionsChecked
                });
            } else {
                totalFrequencyOptionsChecked -= 1;
                this.setState(
                    {
                        totalFrequencyOptionsChecked: totalFrequencyOptionsChecked
                    },
                    () =>
                        this.state.totalFrequencyOptionsChecked < 1
                            ? toast.error(this.state.errorMessage, {
                                autoClose: 5000
                            })
                            : null
                );
            }
        }

        this.setState(prevState => ({
            frequencyOptions: prevState.frequencyOptions.map(option =>
                option.name === value
                    ? { ...option, isChecked: isChecked }
                    : option
            )
        }));

        if (isChecked) {
            this.setState(prevState => ({
                frequencyOptionsChecked: [
                    ...prevState.frequencyOptionsChecked,
                    value
                ]
            }));
        } else {
            const newfrequencyOptionsChecked = this.state.frequencyOptionsChecked.filter(
                option => option !== value
            );
            this.setState({
                frequencyOptionsChecked: newfrequencyOptionsChecked
            });
        }
    }

    render() {
        const { errorMessage } = this.state;

        return (
            <div className="frequency-option">
                <h4>Frequency Options</h4>
                <div className="multi-selection-frequency-option">
                    <div>
                        <p
                            className={
                                this.state.totalFrequencyOptionsChecked <
                                this.props.minCount
                                    ? "errorMessageTransition"
                                    : "hiddenDiv"
                            }
                        >
                            {errorMessage}
                        </p>
                        {this.state.frequencyOptions.map(option => (
                            <Checkbox
                                key={uniqueId()}
                                value={option.name}
                                isChecked={option.isChecked}
                                onCheckboxChange={this.addFrequencyOptions}
                                name={option.name}
                            />
                        ))}
                    </div>
                </div>
            </div>
        );
    }
}

class Frequency extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            frequencyList: [
                { name: "One-time", isChecked: false },
                { name: "Recurring", isChecked: false }
            ],
            // totalNumberCheckedFrequency: 0,
            suppressRecurring: false,
            onlyRecurring: false,
            // frequencyChecked: [],
            visible: false,
            isLoading: false,
            error: null
        };
    }

    componentDidMount() {
        const client_code = this.props.clientCode;
        const pageId = this.props.pageId;
        this.setState({ isLoading: true });
        for (let i = 0; i < length; i++) {
            const field = propertyArray[i];
            let newUrl =
                url +
                "?client_code=" +
                client_code +
                "&page_id=" +
                pageId +
                "&field=" +
                field;
            // console.log(newUrl);
            fetch(newUrl, {
                method: "GET",
                headers: headers,
                mode: "cors"
            })
                .then(response => {
                    if (response.ok) {
                        return response.json();
                    } else {
                        throw new Error("Something went wrong ...");
                    }
                })
                .then(data => {
                    console.log(data);
                    if (data.prop_key.includes("suppressRecurring")) {
                        this.setState({ suppressRecurring: data.value });
                    } else if (data.prop_key.includes("onlyRecurring")) {
                        this.setState({ onlyRecurring: data.value });
                    }
                })

                .catch(error => this.setState({ error, isLoading: false }));
        }
        //not sync, need to figure this out
        if (this.state.suppressRecurring === false) {
            if (this.state.onlyRecurring === false) {
                this.setState(prevState => ({
                    frequencyList: prevState.frequencyList.map(option => ({
                        ...option,
                        isChecked: true
                    }))
                }));
                this.setState({ visible: true });
                this.setState({ totalNumberCheckedFrequency: 2 });
            } else {
                //onlyRecurring === true
                this.setState(prevState => ({
                    frequencyList: prevState.frequencyList.map(option =>
                        option.name === "Recurring"
                            ? { ...option, isChecked: true }
                            : option
                    )
                }));
                this.setState({ visible: true });
                this.setState({ totalNumberCheckedFrequency: 1 });
            }
        } else {
            //suppressRecurring === true
            this.setState(prevState => ({
                frequencyList: prevState.frequencyList.map(option =>
                    option.name === "One-time"
                        ? { ...option, isChecked: true }
                        : option
                )
            }));
            this.setState({ totalNumberCheckedFrequency: 1 });
        }
    }

    componentDidUpdate(prevProps, prevState) {
        let new_value = null;
        let field = null;
        if (prevState.suppressRecurring !== this.state.suppressRecurring) {
            new_value = this.state.suppressRecurring;
            field = "suppressRecurring";
            this.autosaveFrequency(field, new_value);
        } else if (prevState.onlyRecurring !== this.state.onlyRecurring) {
            new_value = this.state.onlyRecurring;
            field = "onlyRecurring";
            this.autosaveFrequency(field, new_value);
        }
    }

    autosaveFrequency = (field, new_value) => {
        const client_code = this.props.clientCode;
        const pageId = this.props.pageId;
        let body = {
            client_code: client_code,
            page_id: pageId,
            field: field,
            new_value: new_value
        };

        fetch(url, {
            method: "POST",
            headers: headers,
            mode: "cors",
            body: JSON.stringify(body)
        })
            .then(response => {
                if (response.ok) {
                    console.log(response);
                    return response.json();
                } else {
                    throw new Error("Something went wrong ...");
                }
            })
            .then(data => {
                console.log(data);
            })
            .catch(error => console.log(error));
    };

    addFrequency = event => {
        const isChecked = event.target.checked;
        const value = event.target.value;

        this.setState(prevState => ({
            frequencyList: prevState.frequencyList.map(option =>
                option.name === value
                    ? { ...option, isChecked: isChecked }
                    : option
            )
        }));

        if (isChecked) {
            if (value === "Recurring") {
                this.setState({ visible: true });
                if (this.state.frequencyList[0].isChecked) {
                    this.setState({ suppressRecurring: false });
                    this.setState({ onlyRecurring: false });
                } else {
                    this.setState({ onlyRecurring: true });
                }
            } else if (value === "One-time") {
                if (this.state.frequencyList[1].isChecked) {
                    this.setState({ suppressRecurring: false });
                    this.setState({ onlyRecurring: false });
                } else {
                    this.setState({ suppressRecurring: true });
                }
            }
        } else {
            if (value === "Recurring") {
                if (this.state.frequencyList[0].isChecked) {
                    this.setState({ visible: false });
                    this.setState({ suppressRecurring: true });
                } else {
                    this.setState({ onlyRecurring: true }); //Need a better way to solve this
                    console.log("Have to select one of the options");
                    toast.error("Must select at least one frequency option", {
                        autoClose: 5000
                    });
                }
            } else if (value === "One-time") {
                if (this.state.frequencyList[1].isChecked) {
                    this.setState({ onlyRecurring: true });
                } else {
                    this.setState({ suppressRecurring: true }); //Need a better way to solve this
                    console.log("Have to select one of the options");
                    toast.error("Must select at least one frequency option", {
                        autoClose: 5000
                    });
                }
            }
        }
    };

    render() {
        const frequency_list = this.state.frequencyList.map(frequency => (
            <label
                key={uniqueId()}
                className="checkbox-inline"
                htmlFor="frequency-selection"
            >
                <input
                    type="checkbox"
                    value={frequency.name}
                    checked={frequency.isChecked}
                    onChange={this.addFrequency}
                />
                {frequency.name}
            </label>
        ));

        // let renderOptions = recurring.isChecked ? <Frequency_options minCount={1} /> : null;
        return (
            <div className="frequency">
                <h4>Frequency</h4>
                <span className="help-block" id="helpBlock">
                    Choose which frequency option are available to the user
                </span>
                <form data-toggle="validator" role="form">
                    <div className="multi-selection-frequency">
                        {frequency_list}
                        <ToastContainer />
                        <div
                            className={
                                this.state.visible
                                    ? "frequencyTransition"
                                    : "hiddenDiv"
                            }
                        >
                            <FrequencyOptions minCount={1} />
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Frequency;
