import React from "react";
import uniqueId from 'lodash/uniqueId';
import downloadS3 from './s3config.js';

// s3.getSignedUrl('getObject', params, function (err, url) {
//     console.log('Your generated pre-signed URL is', url);
// });
var AWS = require('aws-sdk');
var s3 = new AWS.S3({accessKeyId:'AKIAXKCRRSD3GJHJE65C', secretAccessKey:'0lBmJkb6hl885URDMhm+llobNp8WeYLjftjmiy02', region:'us-west-2'});
var urlparams = {Bucket: 'max-client-logos', Key: "194/194-logo-black.png"};
s3.getSignedUrl('getObject', urlparams, function (err, url) {
  console.log('Your generated pre-signed URL is', url);
});
urlparams = {Bucket: 'max-client-logos', Key: "194/kitty.jpg"}
var hapi = s3.getSignedUrl('getObject', urlparams);
console.log(hapi);

class Logo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      choice:"", //Need to somehow set up the initial state so that the user can see the currewnt stage of the logo
    }
  };

  handleChange = (option) => {
    const value = option.target.value;
    this.setState({choice:value});
  }

  createAllUrl = (allkey) => {
    console.log("im a dog");
    let allURL = [];
    const length = allkey.length;
    for (let i = 0; i < length; i++) {
      let params = {Bucket:this.props.bucketName, key:allkey[i]}
      let url = s3.getSignedUrl('getObject', params)
      console.log(url);
      allURL.push(url);
    }
    return allURL;
  }

  render(){
    const allkey = downloadS3(this.props.prefix,this.props.bucketName);
    // const allURL = allkey.map((key) => (
    //   s3.getSignedUrl('getObject', {Bucket:this.props.bucketName, Key:key})
    // ))
    const allURL = this.createAllUrl(allkey);
    console.log(allURL);
    const logoList = allURL.map((url) =>
    <label className="radio-inline">
      <input key={uniqueId()} type="radio" name="inlineRadioOptions" value={url} onChange={this.handleChnage}>
        <img src={url} alt="" />
      </input>
    </label>
  );
    return(
      <div id="logo" className="logo">
          <h3>Logo</h3>
          <span className="help-block" id="helpBlock">Choose page logo</span>
          <div className="logo-options">
            {logoList}
          </div>
          <br/>
      </div>
    );
  }
}

export default Logo;
