var AWS = require('aws-sdk');

const downloadS3 = (prefix, bucketName) => {
  const s3 = new AWS.S3();
  let allKeys = [];
  s3.config.update({
    accessKeyId:'AKIAXKCRRSD3GJHJE65C',
    secretAccessKey:'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',
    region:'us-west-2'
  })

  const params = {
    Bucket: bucketName,
    Delimiter: '/',
    Prefix: prefix,
    StartAfter: prefix
  };
  function listAllKeys(){
    s3.listObjectsV2(params, (err, data) => {
      if (err) {
        console.log(err,err.stack);
      }
      else{
        var contents = data.Contents;
        contents.forEach(function (content) {
            allKeys.push(content.Key);
        });

        if (data.IsTruncated) {
            params.ContinuationToken = data.NextContinuationToken;
            console.log("There are more keys coming up");
            listAllKeys();
        }
      }
    })
    console.log(allKeys);
  }
  listAllKeys();

  // allKeys.map(key => s3.getSignedUrl('getObject', {Bucket:bucketName,Key:key}, function (err, url) {
  //     return url;
  // });

  let url = allKeys.map((key) => (s3.getSignedUrl('getObject', {
    Bucket:bucketName,
    Key:key
  })));

  console.log(url);

  return allKeys;
}

export default downloadS3;
