import React from "react";
import uniqueId from "lodash/uniqueId";

let base64 = require("base-64");
let url =
    "https://cors-anywhere.herokuapp.com/test-api.mobileaxept.com/crm/rock-funds?client_code=016&fund_id=5";
let username = "spencer.grimes@mobileaxept.com";
let password = "Temp654321";

let headers = new Headers();
headers.append(
    "Authorization",
    "Basic " + base64.encode(username + ":" + password)
);
headers.append("Access-Control-Allow-Origin", "*");

class Textbox extends React.Component {
    render() {
        const { name, helptext, onChange, onSubmit, value } = this.props;
        return (
            <div>
                <h4>{name}</h4>
                <span className="help-block" id="helpBlock">
                    {helptext}
                </span>
                <form className="form-inline">
                    <div
                        className="form-group md-2"
                        style={{ marginRight: "5px" }}
                    >
                        <input
                            type="text"
                            className="form-control"
                            id="inputHelpBlock"
                            value={value}
                            name={name}
                            onChange={onChange}
                            placeholder={name}
                        />
                    </div>
                    <button
                        type="submit"
                        className="btn btn-primary"
                        name={name}
                        onSubmit={onSubmit}
                    >
                        Save
                    </button>
                </form>
                <br />
            </div>
        );
    }
}

class Label extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pageText: "",
            panelText: "",
            selectionText: "",
            additionalText: "",
            isLoading: false,
            error: null
        };
    }

    // const propertyArray = ["pageTitle", "fundLabel","title","postFundSelectionHtml"];
    // const length = propertyArray.length;

    componentDidMount() {
        this.setState({ isLoading: true });
        const body = {
            client_code: "194",
            pageId: "null",
            property: "pageTitle"
        };
        fetch(url, {
            method: "Get",
            headers: headers,
            mode: "cors",
            body: JSON.stringfy(body)
        })
            .then(response => {
                if (response.ok) {
                    return response.json();
                } else {
                    throw new Error("Something went wrong ...");
                }
            })
            .then(data => this.setState({ pageText: data, isLoading: false }))
            .catch(error => this.setState({ error, isLoading: false }));
    }

    handleChange = event => {
        if (event.target.name === "Page Title") {
            this.setState({ pageText: event.target.value });
        } else if (event.target.name === "Panel Title") {
            this.setState({ panelText: event.target.value });
        } else if (event.target.name === "Selection Title") {
            this.setState({ selectionText: event.target.value });
        } else if (event.target.name === "Addtional Text") {
            this.setState({ additionalText: event.target.value });
        }
    };

    handleSubmit = event => {
        alert("You submitted successfully");
        event.preventDefault();
    };
    render() {
        if (this.state.isLoading) {
            return (
                <div className="d-flex align-items-center">
                    <strong>Loading...</strong>
                    <div
                        className="spinner-border text-primary ml-auto"
                        role="status"
                        aria-hidden="true"
                    ></div>
                </div>
            );
        }
        return (
            <div id="labels" className="labels">
                <h3>Label</h3>
                <div className="label-page">
                    <Textbox
                        key={uniqueId()}
                        name="Page Title"
                        helptext="This changes the title of the tab"
                        value={this.state.pageText}
                        onChange={this.handleChange}
                        onSubmit={this.handleSubmit}
                    />
                </div>
                <div className="label-panel">
                    <Textbox
                        key={uniqueId()}
                        name="Panel Title"
                        helptext="This changes the title at the top of the page"
                        value={this.state.panelText}
                        onChange={this.handleChange}
                        onSubmit={this.handleSubmit}
                    />
                </div>
                <div className="label-selection">
                    <Textbox
                        key={uniqueId()}
                        name="Selection Title"
                        helptext="This is the label applied above the fund select dropdown menu"
                        value={this.state.selectionText}
                        onChange={this.handleChange}
                        onSubmit={this.handleSubmit}
                    />
                </div>
                <div className="additional-text">
                    <h4>Additional Text</h4>
                    <span className="help-block" id="helpBlock">
                        Additional Text below the fund selection
                    </span>
                    <form>
                        <div className="form-group md-2">
                            <textarea
                                key={uniqueId()}
                                className="form-control"
                                name="Addtional Text"
                                rows="5"
                                onChange={this.handleChange}
                            ></textarea>
                        </div>
                        <div className="form-group">
                            <button
                                type="submit"
                                className="btn btn-primary"
                                name="additionalText"
                                value={this.state.additionalText}
                                onSubmit={this.handleSubmit}
                            >
                                Save
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default Label;
