import React from "react";
import uniqueId from "lodash/uniqueId";
import downloadS3 from "./s3config.js";

// s3.getSignedUrl('getObject', params, function (err, url) {
//     console.log('Your generated pre-signed URL is', url);
// });
var AWS = require("aws-sdk");
var keys = ["194/194-logo-black.png", "194/kitty.jpg"];
var list = [];
var s3 = new AWS.S3({
    accessKeyId: "AKIAXKCRRSD3GJHJE65C",
    secretAccessKey: "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",
    region: "us-west-2"
});
var urlparams = { Bucket: "max-client-logos", Key: keys[0] };
s3.getSignedUrl("getObject", urlparams, function(err, url) {
    list.push(url);
    console.log("Your generated pre-signed URL is", url);
});
urlparams = { Bucket: "max-client-logos", Key: keys[1] };
var hapi = s3.getSignedUrl("getObject", urlparams);
list.push(hapi);

function createAllUrl(allkey) {
    let allURL = [];
    const length = allkey.length;
    console.log(length);
    for (let i = 0; i < length; i++) {
        console.log("im a dog");
        let params = { Bucket: "max-client-logos", Key: allkey[i] };
        let url = s3.getSignedUrl("getObject", params);
        allURL.push(url);
    }
    console.log(allURL);
    return allURL;
}
createAllUrl(keys);

class Logo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            choice: "" //Need to somehow set up the initial state so that the user can see the current stage of the logo
        };
    }

    handleChange = option => {
        const value = option.target.value;
        this.setState({ choice: value });
    };

    // createAllUrl = allkey => {
    //     let allURL = [];
    //     const length = allkey.length;
    //     for (let i = 0; i < length; i++) {
    //         console.log("im a dog");
    //         let params = {
    //             Bucket: this.props.bucketName,
    //             key: allkey["index" === i]["Key"]
    //         };
    //         let url = s3.getSignedUrl("getObject", params);
    //         allURL.push(url);
    //         console.log(i, allURL);
    //     }
    //     return allURL;
    // };

    render() {
        var allkey = () => {
            downloadS3(this.props.prefix, this.props.bucketName).then(
                console.log(allkey)
            );
        };
        var elements = Array.from(allkey);
        console.log(elements);
        allkey = elements;
        console.log(allkey);
        const allURL = allkey.map(key =>
            s3.getSignedUrl("getObject", {
                Bucket: this.props.bucketName,
                Key: key
            })
        );
        console.log(allURL);
        // const allURL = this.createAllUrl(allkey);
        const logoList = allURL.map(url => (
            <label className="radio-inline">
                <input
                    key={uniqueId()}
                    type="radio"
                    name="inlineRadioOptions"
                    value={url}
                    onChange={this.handleChnage}
                >
                    <img src={url} alt="" />
                </input>
            </label>
        ));
        return (
            <div id="logo" className="logo">
                <h3>Logo</h3>
                <span className="help-block" id="helpBlock">
                    Choose page logo
                </span>
                <div className="logo-options">{logoList}</div>
                <br />
            </div>
        );
    }
}

export default Logo;
