    async componentDidMount() {
        const result = await downloadS3(
            this.props.prefix,
            this.props.bucketName
        );
        // console.log("allkey", result);
        this.setState({ options: result, loading: false });
    }

    handleAddLogo = () => {
        this.setState({ showDropZone: true });
    };

    render() {
        if (this.state.loading) {
            return null;
        }
        // var list = [];
        // (async function() {
        //     var allKeys = await downloadS3(
        //         this.props.prefix,
        //         this.props.bucketName
