import React, { useEffect, useState } from "react";
import { useDropzone } from "react-dropzone";
import { ListGroup, ListGroupItem, Button } from "react-bootstrap";
import { CSSTransition, TransitionGroup } from "react-transition-group";
// import uniqueId from "lodash/uniqueId";

const getColor = props => {
    if (props.isDragAccept) {
        return "#00e676";
    }
    if (props.isDragReject) {
        return "#ff1744";
    }
    if (props.isDragActive) {
        return "#2196f3";
    }
    return "#eeeeee";
};

const thumbsContainer = {
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    marginTop: 16
};

const thumb = {
    display: "inline-flex",
    borderRadius: 2,
    border: "1px solid #eaeaea",
    marginBottom: 8,
    marginRight: 8,
    width: 100,
    height: 100,
    padding: 4,
    boxSizing: "border-box"
};

const thumbInner = {
    display: "flex",
    minWidth: 0,
    overflow: "hidden"
};

const img = {
    display: "block",
    width: "auto",
    height: "100%"
};

function Dropzone(props) {
    const [files, setFiles] = useState([]);
    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        multiple: true,
        accept: "image/*",
        onDrop: acceptedFiles => {
            console.log(acceptedFiles);
            setFiles(
                acceptedFiles.map(file =>
                    Object.assign(file, {
                        preview: URL.createObjectURL(file)
                    })
                )
            );
        }
    });

    //with inner or not? inner will shrink the size
    const thumbs = (
        <TransitionGroup>
            {files.map(file => (
                <CSSTransition key={file.path} timeout={500} classNames="item">
                    <div style={thumb} key={file.name}>
                        <div style={thumbInner}>
                            <img src={file.preview} alt="" style={img} />
                        </div>
                    </div>
                </CSSTransition>
            ))}
        </TransitionGroup>
    );

    // const thumbs = files.map(file => (
    //     <div style={thumb} key={file.name}>
    //         <img src={file.preview} style={img} />
    //     </div>
    // ));

    const removefile = name => () => {
        console.log("hi there");
        const newFiles = files.filter(item => item.name !== name);
        setFiles(newFiles);
    };

    const removeAll = () => {
        setFiles([]);
    };

    useEffect(
        () => () => {
            // Make sure to revoke the data uris to avoid memory leaks
            files.forEach(file => URL.revokeObjectURL(file.preview));
        },
        [files]
    );

    const acceptedFilesItems = (
        <ListGroup>
            <TransitionGroup className="fileList">
                {files.map(file => (
                    <CSSTransition
                        key={file.path}
                        timeout={500}
                        classNames="item"
                    >
                        <ListGroupItem>
                            {file.path} - {file.size} bytes{" "}
                            <Button
                                size="sm"
                                className="remove-btn"
                                variant="danger"
                                onClick={removefile(file.name)}
                                value={file.name}
                            >
                                X
                            </Button>
                        </ListGroupItem>
                    </CSSTransition>
                ))}
            </TransitionGroup>
        </ListGroup>
    );

    // <em>(Only *.jpeg and *.png images will be accepted)</em>);
    const dragInActiveText = (
        <>
            <p>Drag and drop some files here, or click to select files</p>
            <em>(Only *.jpeg and *.png images will be accepted)</em>
        </>
    );
    const dragActiveText = <p>Drop it like hot</p>;

    return (
        <div className="container">
            <div {...getRootProps({ className: "dropzone" })}>
                <input {...getInputProps()} />
                {isDragActive ? dragActiveText : dragInActiveText}
            </div>
            <aside style={thumbsContainer}>{thumbs}</aside>
            <aside>{acceptedFilesItems}</aside>
            {files.length > 0 && (
                <Button
                    variant="danger"
                    className="remove-btn"
                    onClick={removeAll}
                    variant="danger"
                >
                    Remove All
                </Button>
            )}
        </div>
    );
}

export default Dropzone;
