import React from "react";
import uniqueId from "lodash/uniqueId";
import { SketchPicker } from "react-color";

function generator() {
    var cp = document.getElementById("cp1");
    cp.colorpicker();
}

class ColorpickerComponent extends React.Component {
    colorpicker = () => {
        document.getElementById(this.props.id).colorpicker();
    };

    render() {
        const { value, className, title, handleChange } = this.props;
        return (
            <div className={className}>
                <h5>{title}</h5>
                <div className="input-group colorpicker-component">
                    <input
                        id="cp1"
                        type="text"
                        className="form-control"
                        defaultValue={value}
                        name={title}
                        onChange={handleChange}
                    />
                    <span className="input-group-addon">
                        <i></i>
                    </span>
                </div>
            </div>
        );
    }
}

class Colors extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            displayColorPicker: false,
            Background: "#fff",
            Header: "#fff",
            SelectionButton: "#fff"
        };
    }

    handleChange = color => {};

    render() {
        return (
            <div id="colors" className="colors">
                <h4>Colors</h4>
                <span className="help-block" id="helpBlock">
                    Change the color of the page
                </span>
                <ColorpickerComponent
                    className="background-color"
                    value={this.state.Background}
                    title="Background"
                    onChange={this.handleChange}
                />
            </div>
        );
    }
}

export default Colors;
