//     console.log(length);
//     for (let i = 0; i < length; i++) {
//         console.log("im a dog");
//         let params = { Bucket: "max-client-logos", Key: allkey[i] };
//         let url = s3.getSignedUrl("getObject", params);
//         allURL.push(url);
//     }
//     console.log(allURL);
//     return allURL;
// }
// createAllUrl(keys);

class Logo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showDropZone: false,
            choice: "",
            options: [],
            error: null,
            isLoading: false //Need to somehow set up the initial state so that the user can see the current stage of the logo
        };
        // this.getKeys = this.getKeys.bind(this);
    }

    handleChange = option => {
        const value = option.target.value;
        this.setState({ choice: value });
    };
    //

    // createAllUrl = allkey => {
    //     let allURL = [];
    //     const length = allkey.length;
    //     for (let i = 0; i < length; i++) {
    //         console.log("im a dog");
    //         let params = {
    //             Bucket: this.props.bucketName,
    //             key: allkey["index" === i]["Key"]
    //         };
    //         let url = s3.getSignedUrl("getObject", params);
    //         allURL.push(url);
    //         console.log(i, allURL);
    //     }
    //     return allURL;
    // };
    async componentDidMount() {
        this.setState({ isLoading: true });
        try {
            const result = await downloadS3(
                this.props.prefix,
                this.props.bucketName
            );
            // console.log("allkey", result);
            this.setState({ options: result, isLoading: false });
        } catch (error) {
            this.setState({ error, isLoading: false });
        }
    }

    handleClick = () => {
        this.setState(prevState => ({ showDropZone: !prevState.showDropZone }));
    };

    render() {
        if (this.state.loading) {
            return (
                <div className="d-flex align-items-center">
                    <strong>Loading...</strong>
                    <div
                        className="spinner-border text-primary ml-auto"
                        role="status"
                        aria-hidden="true"
                    ></div>
                </div>
            );
        }

        // var list = [];
        // (async function() {
        //     var allKeys = await downloadS3(
        //         this.props.prefix,
        //         this.props.bucketName
        //     );
        //     list = allKeys;
        // })();

        var list = this.state.options;
        const allURL = list.map(key =>
            s3.getSignedUrl("getObject", {
                Bucket: this.props.bucketName,
                Key: key,
                Expires: 300
            })
        );
        // console.log("allURL", allURL);
        // const allURL = this.createAllUrl(allkey);
        const logoList = allURL.map(url => (
            <div key={uniqueId()} className="container col-md-12">
                <label className="radio-inline">
                    <input
                        type="radio"
                        name="inlineRadioOptions"
                        value={url}
                        onChange={this.handleChnage}
                    />
                    <img className="logoImage" src={url} alt="" />
                </label>
            </div>
        ));

        const backgroundColor = {
            background: "00aabb"
        };
        return (
            <div id="logo" className="logo">
                <h3>Logo</h3>
                <span className="help-block" id="helpBlock">
                    Choose page logo
                </span>
                <div className="logo-options" style={backgroundColor}>
                    {logoList}
                </div>
                <br />
                <form className="logo-upload form-group row">
                    <div className="col-sm-10 col-md-12">
                        <button
                            name="button"
                            className="btn btn-primary"
                            type="submit"
                            onClick={this.handleClick}
                        >
                            Add a new logo
                        </button>
                    </div>
                </form>
                <Dropzone prefix={this.props.prefix} />
            </div>
        );
    }
}

export default Logo;
