import React from "react";
import uniqueId from 'lodash/uniqueId';

class Textbox extends React.Component{
  render() {
    const {name, helptext, onChange, onSubmit, value} = this.props;
    return (
      <div>
        <h4>{name}</h4>
        <span className="help-block" id="helpBlock">{helptext}</span>
        <form className="form-inline">
          <div className="form-group md-2">
            <input type="text" className="form-control" id="inputHelpBlock" value={value} name={name} onChange={onChange} placeholder={name} />
          </div>
          <button type="submit" className="btn btn-primary" name={name} onSubmit={onSubmit}>Save</button>
        </form>
        <br/>
      </div>
    );
  }
}

class Label extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pageText:"",
      panelText:"",
      selectionText:"",
      additionalText:""
    }
  }

  handleChange = (event) => {
    if(event.target.name === "Page Title"){
      this.setState({pageText: event.target.value});
    }
    else if (event.target.name === "Panel Title") {
      this.setState({panelText: event.target.value});
    }
    else if (event.target.name === "Selection Title") {
      this.setState({selectionText: event.target.value});
    }
    else if (event.target.name === "Addtional Text") {
      this.setState({additionalText: event.target.value});
    }
  }

  handleSubmit = (event) => {
    alert("You submitted successfully");
    event.preventDefault();
  }
  render() {
    return(
      <div id="logo" className="logo">
          <h3>Logo</h3>
          <span className="help-block" id="helpBlock">Choose page logo</span>
          <br/>
          <div className="label-page">
            <Textbox key={uniqueId()} name="Page Title" helptext="This changes the title of the tab" value={this.state.pageText} onChange={this.handleChange} onSubmit={this.handleSubmit} />
          </div>
          <div className="label-panel">
            <Textbox key={uniqueId()} name="Panel Title" helptext="This changes the title at the top of the page" value={this.state.panelText} onChange={this.handleChange} onSubmit={this.handleSubmit} />
          </div>
          <div className="label-selection">
            <Textbox key={uniqueId()} name="Selection Title" helptext="This is the label applied above the fund select dropdown menu" value={this.state.selectionText} onChange={this.handleChange} onSubmit={this.handleSubmit} />
          </div>
            <div className="additional-text">
              <h4>Additional Text</h4>
              <span className="help-block" id="helpBlock">Additional Text below the fund selection</span>
              <form>
                <div className="form-group md-2">
                  <textarea key={uniqueId()} className="form-control" name="Addtional Text" rows="5" onChange={this.handleChange} ></textarea>
                </div>
                <div className="form-group">
                  <button type="submit" className="btn btn-primary" name="additionalText" value={this.state.additionalText} onSubmit={this.handleSubmit}>Save</button>
                </div>
              </form>
            </div>
      </div>
    )
  }
}

export default Label;
