import React from "react";
import uniqueId from "lodash/uniqueId";

class RadioOption extends React.Component {
    render() {
        const {
            id,
            className,
            optionName,
            isChecked,
            value,
            onChange
        } = this.props;
        return (
            <div className={className}>
                <div className="radio">
                    <label htmlFor={"optionsRadios" + id}>
                        <input
                            id={"optionsRadios" + id}
                            type="radio"
                            name="optionsRadios"
                            value={value}
                            onChange={onChange}
                            checked={isChecked}
                        />
                        {optionName}
                    </label>
                </div>
            </div>
        );
    }
}

class Layout extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            layoutOptions: [
                {
                    className: "numberFirst",
                    name: "Number First",
                    isChecked: false
                },
                {
                    className: "fundSelectionFirst",
                    name: "Fund Seletion First",
                    isChecked: false
                }
            ],
            layoutChosen: ""
        };
    }

    handleChange = event => {
        console.log("1", this.state.layoutOptions[0].isChecked);
        console.log("2", this.state.layoutOptions[1].isChecked);
        const isChecked = event.target.checked;
        console.log(isChecked);
        const value = event.target.value;
        console.log(value);

        this.setState({ layoutChosen: value });
        this.setState(prevState => {
            prevState.layoutOptions.map(option =>
                option.className === value
                    ? { ...option, isChecked: isChecked }
                    : { ...option, isChecked: !option.isChecked }
            );
        });
        console.log(this.state.layoutOptions[0].isChecked);
        console.log(this.state.layoutOptions[1].isChecked);
    };

    render() {
        const optionList = this.state.layoutOptions.map((option, i) => (
            <RadioOption
                id={i}
                key={uniqueId()}
                className={option.className}
                optionName={option.name}
                value={option.className}
                isChecked={option.isChecked}
                onChange={this.handleChange}
            />
        ));
        return (
            <div id="layout" className="layout">
                <h4>Layout</h4>
                <span className="help-block" id="helpBlock">
                    Choose the page layout
                </span>
                {optionList}
            </div>
        );
    }
}

export default Layout;
