import React from "react";
import uniqueId from "lodash/uniqueId";

let base64 = require("base-64");
let url = "https://test-api.mobileaxept.com/micro_service/page-properties";
let username = "spencer.grimes@mobileaxept.com";
let password = "Temp654321";
let headers = {
    Authorization: "Basic " + base64.encode(username + ":" + password),
    "Content-Type": "application/json"
};

const propertyArray = ["194.null.donateLayout-1", "194.null.donateLayout-2"];
const length = propertyArray.length;

class RadioOption extends React.Component {
    render() {
        const {
            id,
            className,
            optionName,
            isChecked,
            value,
            onChange
        } = this.props;
        return (
            <div className={className}>
                <div className="radio">
                    <label htmlFor={"optionsRadios" + id}>
                        <input
                            id={"optionsRadios" + id}
                            type="radio"
                            name={optionName}
                            value={value}
                            onChange={onChange}
                            checked={isChecked}
                        />
                        {optionName}
                    </label>
                </div>
            </div>
        );
    }
}

class Layout extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            layoutOptions: [
                {
                    className: "numberFirst",
                    name: "Number First",
                    isChecked: false
                },
                {
                    className: "fundSelectionFirst",
                    name: "Fund Seletion First",
                    isChecked: false
                }
            ],
            layoutChosen: "",
            isLoading: false,
            error: null
        };
    }

    componentDidMount() {
        const client_code = this.props.clientCode;
        const pageId = this.props.pageId;
        this.setState({ isLoading: true });
        for (let i = 0; i < length; i++) {
            let newUrl =
                url +
                "?client_code=" +
                client_code +
                "&page_id=" +
                pageId +
                "&field=" +
                propertyArray[i];
            console.log(newUrl);

            fetch(newUrl, {
                method: "GET",
                headers: headers,
                mode: "cors"
            })
                .then(response => {
                    if (response.ok) {
                        return response.json();
                    } else {
                        throw new Error("Something went wrong ...");
                    }
                })
                .then(data => {
                    console.log(data);
                })
                .catch(error => this.setState({ error, isLoading: false }));
        }
    }

    handleChange = option => {
        const isChecked = option.target.checked;
        // console.log(isChecked);
        const value = option.target.value;
        console.log(value);

        this.setState({ layoutChosen: value });
        this.setState(
            prevState => ({
                layoutOptions: prevState.layoutOptions.map(option =>
                    option.className === value
                        ? { ...option, isChecked: isChecked }
                        : { ...option, isChecked: !isChecked }
                )
            }),
            function() {
                console.log(
                    "numberFirst",
                    this.state.layoutOptions[0].isChecked
                );
                console.log(
                    "fundSelectionFirst",
                    this.state.layoutOptions[1].isChecked
                );
            }
        );
    };

    render() {
        if (this.state.error) {
            return <p>{this.state.error.message}</p>;
        }
        if (this.state.isLoading) {
            return (
                <div className="loader" id="loader-1">
                    <span className="sr-only">Loading...</span>
                </div>
            );
        }
        const optionList = this.state.layoutOptions.map((option, i) => (
            <RadioOption
                id={i}
                key={uniqueId()}
                className={option.className}
                optionName={option.name}
                value={option.className}
                isChecked={option.isChecked}
                onChange={this.handleChange}
            />
        ));
        return (
            <div id="layout" className="layout">
                <h3>Layout</h3>
                <span className="help-block" id="helpBlock">
                    Choose the page layout
                </span>
                {optionList}
            </div>
        );
    }
}

export default Layout;
